import { useState, useEffect } from "react";
import axios, { all } from 'axios'

const baseUrl = "https://studies.cs.helsinki.fi/restcountries/"

const requestCountryInfo = (country) => {
  const request = axios.get(baseUrl + `api/name/${country.toLowerCase()}`)
  const dataPromise = request.then(response => response.data)
  return dataPromise
}

const SearchResult = ({filterResult, dataReceived}) => {  
  if (!dataReceived) return <div>Data has not yet been received</div>
  else if (filterResult.length === 1) {
    requestCountryInfo(filterResult[0])
    .then(data => console.log(data))
  }
  else if(filterResult.length === 0) return <div>No match</div>
  else if (filterResult.length > 10) {
    return <div>Too many matches. Specify another filter</div>
  } else {
    return (
      filterResult.map((c, i) => <div key={i}>{c}</div>)
    )
  }
}

