import { useState, useEffect } from "react";
import axios, { all } from 'axios'

const baseUrl = "https://studies.cs.helsinki.fi/restcountries/"

const request = (country) => {
  axios.get(baseUrl + `api/${country}`)
  .then(response => response.data)
}

const SearchResult = ({filterResult, dataReceived}) => {  
  if (!dataReceived) return <div>Data has not yet been received</div>
  else if (filterResult.length === 1) {
    console.log(request(filterResult[0]))
  }
  else if(filterResult.length === 0) return <div>No match</div>
  else if (filterResult.length > 10) {
    return <div>Too many matches. Specify another filter</div>
  } else {
    return (
      filterResult.map((c, i) => <div key={i}>{c}</div>)
    )
  }
}

function App() {  
  const [country, setCountry] = useState('')
  const [countriesWithFilter, setCountriesWithFilter] = useState([])
  const [allCountries, setAllCountries] = useState([])

  // get list of all countries
  useEffect(() => {
    axios.get(baseUrl + "api/all")
    .then(response => {
      const allCountriesNew = response.data.map(obj => obj.name.common)
      setAllCountries(allCountriesNew)          
    })
  }, [])

  useEffect(() => {
    if (allCountries.length !== 0) {
      const countriesWithFilterNew = allCountries.filter(c => c.toUpperCase().includes(country.toUpperCase()))
      setCountriesWithFilter(countriesWithFilterNew)      
    } 
  }, [country])

  const onSearch = (event) => {
    setCountry(event.target.value)
  }

  return (
    <div>
      find countries&nbsp;
      <input value={country ?? ''} onChange={onSearch} />
      <SearchResult filterResult={countriesWithFilter} dataReceived={allCountries.length !== 0 ? true : false} />
    </div>
  );
}

export default App;
