import { useState, useEffect } from "react";
import axios from 'axios'

const SearchResult = filterResult => {  
  return null
}

function App() {
  const baseUrl = "https://studies.cs.helsinki.fi/restcountries/"
  const [country, setCountry] = useState('')
  const [countriesWithFilter, setCountriesWithFilter] = useState([])
  const [allCountries, setAllCountries] = useState([])

  // get list of all countries
  useEffect(() => {
    axios.get(baseUrl + "api/all")
    .then(response => {
      const newAllCountries = response.data.map(obj => obj.name.common)
      setAllCountries(newAllCountries)
    })
  }, [])

  const onSearch = (event) => {
    const newCountriesWithFilter = allCountries.filter(c => c.toUpperCase().startsWith(country.toUpperCase()))
    setCountriesWithFilter(newCountriesWithFilter)
    console.log(countriesWithFilter)
    setCountry(event.target.value)
  }

  return (
    <div>
      find countries&nbsp;
      <input value={country} onChange={onSearch} />
      <SearchResult filterResult={countriesWithFilter} />
    </div>
  );
}

export default App;
