import { useState, useEffect } from "react";
import axios from 'axios'

function App() {
  const baseUrl = "https://studies.cs.helsinki.fi/restcountries/"
  const [country, setCountry] = useState('')
  const [countriesWithFilter, setCountriesWithFilter] = useState([])
  let allCounries = []

  // get list of all countries
  useEffect(() => {
    axios.get(baseUrl + "api/all")
    .then(response => {
      allCounries = response.data.map(obj => obj.name.common)
    })
  }, [])

  useEffect(() => {
    if (allCounries) {
      const newCountriesWithFilter = allCounries.filter(c => c.toUpperCase().startsWith(country.toUpperCase()))
      console.log(newCountriesWithFilter.length)
    }
  }, [country])

  const onSearch = (event) => {
    setCountry(event.target.value)
  }

  return (
    <div>
      find countries&nbsp;
      <input value={country} onChange={onSearch} />
    </div>
  );
}

export default App;
