import { useState, useEffect } from "react";
import axios from 'axios'

function App() {
  const baseUrl = "https://studies.cs.helsinki.fi/restcountries/"
  const [country, setCountry] = useState('')

  useEffect(() => {
    if (country) {
      console.log('fetching information about countries...')
      axios.get(baseUrl)
      .then(response => {
        console.log(response.data)
      })
    }
  }, [country])

  const onSearch = (event) => {
    setCountry(event.target.value)
  }

  return (
    <div>
      find countries&nbsp;
      <input value={country} onChange={onSearch} />
      <h2>Rest Countries</h2>

      <p>Get information about countries via a RESTful API</p>

      <table>
        <tr>
          <th>endpoint</th>
          <th>description</th>
        </tr>
        <tr>
          <td><a href='api/all'>/api/all</a></td>
          <td>All countries</td>
        </tr>
        <tr>
          <td><a href='api/name/finland'>/api/name/{name}</a></td>
          <td>Search by countryâ€™s full name. It can be the common or official value</td>
        </tr>
      </table>
    </div>
  );
}

export default App;
