import { useState, useEffect } from "react";
import axios from 'axios'

function App() {
  const baseUrl = "https://studies.cs.helsinki.fi/restcountries/"
  const [country, setCountry] = useState('')
  const [countriesWithFilter, setCountriesWithFilter] = useState([])
  const [allCountries, setallCountries] = useState([])

  // get list of all countries
  useEffect(() => {
    axios.get(baseUrl + "api/all")
    .then(response => {
      setallCountries(response.data.map(obj => obj.name.common))   
    })
  }, [])

  useEffect(() => {
    if (allCountries.length !== 0) {
      setCountriesWithFilter(allCountries.filter(c => c.toUpperCase().startsWith(country.toUpperCase())))
    }
  }, [country])

  const onSearch = (event) => {
    setCountry(event.target.value)
  }

  const searchResult = () => {
    if (countriesWithFilter.length > 10) {
      return countriesWithFilter.map(c => <div>{c}</div>)
    } else {
      console.log('Many results')
      return countriesWithFilter.map(c => <div>{c}</div>)
    }
  }

  return (
    <div>
      find countries&nbsp;
      <input value={country} onChange={onSearch} />
      <div>
        {searchResult()}
      </div>
    </div>
  );
}

export default App;
