import { useState, useEffect } from "react";
import axios, { all } from 'axios'

const SearchResult = ({filterResult}) => {  
  if (filterResult.length === 0) return null
  else if (filterResult.length > 10) {
    return <div>Too many matches. Specify another filter</div>
  } else {
    return (
      filterResult.map((c, i) => <div key={i}>{c}</div>)
    )
  }
}

function App() {
  const baseUrl = "https://studies.cs.helsinki.fi/restcountries/"
  const [country, setCountry] = useState('')
  const [countriesWithFilter, setCountriesWithFilter] = useState([])
  const [allCountries, setAllCountries] = useState([])

  // get list of all countries
  useEffect(() => {
    axios.get(baseUrl + "api/all")
    .then(response => {
      const allCountriesNew = response.data.map(obj => obj.name.common)
      setAllCountries(allCountriesNew)          
    })
  }, [])

  useEffect(() => {
    if (allCountries.length !== 0) {
      const countriesWithFilterNew = allCountries.filter(c => c.toUpperCase().startsWith(country.toUpperCase()))
      setCountriesWithFilter(countriesWithFilterNew)      
    } 
  }, [country])

  const onSearch = (event) => {
    setCountry(event.target.value)
  }

  return (
    <div>
      find countries&nbsp;
      <input value={country ?? ''} onChange={onSearch} />
      <SearchResult filterResult={countriesWithFilter} />
    </div>
  );
}

export default App;
