import { useState, useEffect } from "react";
import axios, { all } from 'axios'

const SearchResult = filterResult => {  
  return null
}

function App() {
  const baseUrl = "https://studies.cs.helsinki.fi/restcountries/"
  const [country, setCountry] = useState('country')
  const [countriesWithFilter, setCountriesWithFilter] = useState([])
  var allCountries

  // get list of all countries
  useEffect(() => {
    axios.get(baseUrl + "api/all")
    .then(response => {
      allCountries = response.data.map(obj => obj.name.common)
      console.log(`Data about countries has been received. ${allCountries.length}`)       
    })
  }, [])

  useEffect(() => {
    console.log("Value of country has been changed!")
  }, [country])

  const onSearch = (event) => {
    setCountry(event.target.data)
  }

  return (
    <div>
      find countries&nbsp;
      <input value={country} onChange={onSearch} />
      <SearchResult filterResult={countriesWithFilter} />
    </div>
  );
}

export default App;
