import { useState } from 'react'

const SearchFilter = ({filterVal, setNewFilter}) => (
  <div>
    filter shown with <input value={filterVal} onChange={e => setNewFilter(e.target.value)}/>
  </div>
)

const PersonForm = ({}) => {
  <form onSubmit={addName}>
    <div>
      name: <input value={newName} onChange={e => setNewName(e.target.value)}/>
    </div>
    <div>
      number: <input value={newNumber} onChange={e => setNewNumber(e.target.value)}/>
    </div>
    <div>
      <button type="submit">add</button>
    </div>
  </form>

}

const App = () => {
  const [persons, setPersons] = useState([
      { name: 'Arto Hellas', number: '040-123456', id: 1 },
      { name: 'Ada Lovelace', number: '39-44-5323523', id: 2 },
      { name: 'Dan Abramov', number: '12-43-234345', id: 3 },
      { name: 'Mary Poppendieck', number: '39-23-6423122', id: 4 }
  ])
  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [filterParam, setFilterParam] = useState('')

  const addName = (event) => {
    event.preventDefault()
    if (persons.filter(p => p.name === newName).length != 0) {
      alert(`${newName} is already added to phonebook`)
    } else {
      const personObj = {name: newName, number: newNumber}
      setPersons(persons.concat(personObj))
      setNewName('')
      setNewNumber('')
    }
  }

  const showOnlyFiltered = () => {
    const newPersons = persons.filter(p => p.name.toUpperCase().startsWith(filterParam.toUpperCase()))
    return (newPersons.map((person, i) => <div key={i}>{person.name} {person.number}</div>))
  }

  return (
    <div>
      <h2>Phonebook</h2>

      <SearchFilter setNewFilter={setFilterParam} />

      <h2>Add a new</h2>

      <h2>Numbers</h2>
      {showOnlyFiltered()}
    </div>
  )
}

export default App
